import React, {Component} from 'react';
import HomeGamesDetails from '../data/homeGames.json';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Card, Row, Col,} from 'react-bootstrap';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useLocation,
    useParams
  } from "react-router-dom";


function Pagination() {
    return (
        <Router>
        <GameSwitch />
        </Router>
    );
}

function GameSwitch() {
let location = useLocation();
let background = location.state && location.state.background;
return (
  <div>
    <Switch location={background || location}>
      <Route exact path="/" children={<HomeGames />} />
      <Route path="/game/:id" children={<GameView />} />
    </Switch>
  </div>
);
}

function GameView() {
    let { id } = useParams();
    let HomeGameDetails = HomeGamesDetails[parseInt(id, 10)];
    return (
        <Container>
        <Row>
        <Col>
            <Card>
                <Card.Img variant="top" src={HomeGameDetails.src} />
                <Card.Body>
                    <Card.Title>
                        {HomeGameDetails.name}
                    </Card.Title>
                    <Card.Text>
                        {HomeGameDetails.releaseYear}
                    </Card.Text>
                    <Card.Text>
                        {HomeGameDetails.studio}
                    </Card.Text>
                </Card.Body>               
            </Card>
        </Col>
        </Row>
        </Container>
    )            
  }


class HomeGames extends Component {
    constructor (props){
        super(props)
        this.state = {
          HomeGameDetails: HomeGamesDetails
        }
      }
      render(){
        const {HomeGameDetails} = this.state
    return (       
        <Row>
        {HomeGameDetails.map((HomeGameDetail) =>{
            return(
                <Col md={6}>
                <Card>
                    <Card.Img variant="top" src={HomeGameDetail.src} />
                    <Card.Body>
                        <Card.Title>
                            {HomeGameDetail.name}
                        </Card.Title>
                        <Card.Text>
                            {HomeGameDetail.releaseYear}
                        </Card.Text>
                        <Card.Text>
                            {HomeGameDetail.studio}
                        </Card.Text>
                        <Link to={`/game/${HomeGameDetail.id}`} variant="primary">read more...</Link>
                    </Card.Body>

                </Card>
                </Col>
            )
        })}           
</Row>
    )
}
} 
export default Pagination;
