import React from 'react';
import HomeGamesDetails from '../data/homeGames.json';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Card, Row, Col, Button } from 'react-bootstrap';

function HomeGames() {
    return (
        <Row>
            {HomeGamesDetails.map((HomeGamesDetail,index) =>{
                return(
                    <Card style={{ width: '18rem' }}>
                        <Card.Img variant="top" src={HomeGamesDetail.src} />
                        <Card.Body>
                            <Card.Title>
                                {HomeGamesDetail.name}
                            </Card.Title>
                            <Card.Text>
                                {HomeGamesDetail.releaseYear}
                            </Card.Text>
                            <Card.Text>
                                {HomeGamesDetail.studio}
                            </Card.Text>
                            <Card.Link variant="primary">read more...</Card.Link>
                        </Card.Body>
                    </Card>
                )
            })}           
        </Row>
    )
}

export default HomeGames;
