import React, {Component} from 'react';
import Sidebars from './Sidebars';
import HomeGames from './HomeGames';
import SidebarDetails from '../data/sidebars.json';
import HomeGamesDetails from '../data/homeGames.json';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col, Button, Card } from 'react-bootstrap';

class Games extends Component {
  constructor (props){
    super(props)
    this.sortYa = this.sortYa.bind(this)
    this.state = {
      HomeGameDetails: HomeGamesDetails
    }
  }



  sortYa(){
    const {HomeGameDetails} = this.state
    this.setState({
      HomeGameDetails: HomeGamesDetails.sort((a,b)=>a.releaseYear-b.releaseYear)
    })
  };

  sortYd(){
    const {HomeGameDetails} = this.state
    this.setState({
      HomeGameDetails: HomeGamesDetails.sort((a,b)=>b.releaseYear-a.releaseYear)
    })
  };

  render(){
    const {HomeGameDetails} = this.state
  return (  
    <Container text-center>
      <h1 className="text-center mb-5 mt-3">Online Games</h1>     
      <Row className="mt-4 mb-4 row row-eq-height">
        <Col lg>
          <Button onClick={()=>{this.sortAa()}} variant="success" size="lg" block>{ SidebarDetails[0].content }</Button>
        </Col>
        <Col lg>
          <Button onClick={()=>{this.sortAd()}} variant="primary" size="lg" block>{ SidebarDetails[1].content }</Button>
        </Col>
        <Col lg>
          <Button onClick={(e)=>{this.sortYa()}} variant="success" size="lg" block>{ SidebarDetails[2].content }</Button>
        </Col>
        <Col lg>
          <Button onClick={(e)=>{this.sortYd(e)}} variant="primary" size="lg" block>{ SidebarDetails[3].content }</Button>
        </Col>
        <Col lg>
          <Button onClick={()=>{this.reset()}} variant="danger" size="lg" block>{ SidebarDetails[4].content }</Button>
        </Col>
      </Row>

      <Row>
            {HomeGameDetails.map((HomeGameDetail) =>{
                return(
                    <Col md={6}>
                    <Card>
                        <Card.Img variant="top" src={HomeGameDetail.src} />
                        <Card.Body>
                            <Card.Title>
                                {HomeGameDetail.name}
                            </Card.Title>
                            <Card.Text>
                                {HomeGameDetail.releaseYear}
                            </Card.Text>
                            <Card.Text>
                                {HomeGameDetail.studio}
                            </Card.Text>
                            <Card.Link variant="primary">read more...</Card.Link>
                        </Card.Body>

                    </Card>
                    </Col>
                )
            })}           
    </Row>

    </Container>
  );
}
}

export default Games;
