import React, {Component} from 'react';
import Sidebars from './Sidebars';
import HomeGames from './HomeGames';
import SidebarDetails from '../data/sidebars.json';
import HomeGamesDetails from '../data/homeGames.json';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col, Button, } from 'react-bootstrap';

class Games extends Component {
  sortYa(){
    const HomeGamesDetails = HomeGamesDetails.sort((a,b)=>a.releaseYear-b.releaseYear)
  };

  render(){
  return (  
    <Container text-center>
      <h1 className="text-center mb-5 mt-3">Online Games</h1>     
          <Row className="mt-4 mb-4 row row-eq-height">
        <Col lg>
          <Button onClick={()=>{this.sortAa()}} variant="success" size="lg" block>{ SidebarDetails[0].content }</Button>
        </Col>
        <Col lg>
          <Button onClick={()=>{this.sortAd()}} variant="primary" size="lg" block>{ SidebarDetails[1].content }</Button>
        </Col>
        <Col lg>
          <Button onClick={()=>{this.sortYa()}} variant="success" size="lg" block>{ SidebarDetails[2].content }</Button>
        </Col>
        <Col lg>
          <Button onClick={()=>{this.sortYd()}} variant="primary" size="lg" block>{ SidebarDetails[3].content }</Button>
        </Col>
        <Col lg>
          <Button onClick={()=>{this.reset()}} variant="danger" size="lg" block>{ SidebarDetails[4].content }</Button>
        </Col>
      </Row>
      <Row>
        <HomeGames />
      </Row>
    </Container>
  );
}
}

export default Games;
