import React, {Component} from 'react';
import HomeGamesDetails from '../data/homeGames.json';
import HomeGames from './HomeGames';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Card, Row, Col,} from 'react-bootstrap';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useLocation,
    useParams
  } from "react-router-dom";


export default function Pagination() {
    return (
        <Router>
        <GameSwitch />
        </Router>
    );
}

function GameSwitch() {
let location = useLocation();
let background = location.state && location.state.background;
return (
  <div>
    <Switch location={background || location}>
      <Route exact path="/" children={<HomeGames />} />
      <Route path="/game/:id" children={<GameView />} />
    </Switch>
  </div>
);
}

function GameView() {
    let { id } = useParams();
    let HomeGameDetail = HomeGamesDetails[parseInt(id, 10)];
    return (
        <Col md={6}>
            <Card>
                <Card.Img variant="top" src={HomeGameDetail.src} />
                    <Card.Body>
                        <Card.Title>
                            {HomeGameDetail.name}
                        </Card.Title>
                        <Card.Text>
                            {HomeGameDetail.releaseYear}
                        </Card.Text>
                        <Card.Text>
                            {HomeGameDetail.studio}
                        </Card.Text>
                    </Card.Body>

            </Card>
        </Col>
    )            
  }
