import React, {Component} from 'react';
import SidebarDetails from '../data/sidebars.json';
import HomeGamesDetails from '../data/homeGames.json';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col, Button } from 'react-bootstrap';




class Sidebars extends Component {
  constructor (props){
    super(props)
    this.sortYa = this.sortYa.bind(this)
    this.state = {
      HomeGameDetails: HomeGamesDetails
    }
  }

  sortAa(){
    const {HomeGameDetails} = this.state
    this.setState({
      HomeGameDetails: HomeGamesDetails.sort((a,b)=>a.sd.localeCompare(b.sd))
    })
  };

  sortAd(){
    const {HomeGameDetails} = this.state
    this.setState({
      HomeGameDetails: HomeGamesDetails.sort((a,b)=>b.sd.localeCompare(a.sd))
    })
  };

  sortYa(){
    const {HomeGameDetails} = this.state
    this.setState({
      HomeGameDetails: HomeGamesDetails.sort((a,b)=>a.releaseYear-b.releaseYear)
    })
  };

  sortYd(){
    const {HomeGameDetails} = this.state
    this.setState({
      HomeGameDetails: HomeGamesDetails.sort((a,b)=>b.releaseYear-a.releaseYear)
    })
  };

  reset(){
    const {HomeGameDetails} = this.state
    this.setState({
      HomeGameDetails: HomeGamesDetails.sort((a,b)=>a.id-b.id)
    })
  }

  render(){

    return (
      <Row className="mt-4 mb-4 row row-eq-height">
        <Col lg>
          <Button onClick={()=>{this.sortAa()}} variant="success" size="lg" block>{ SidebarDetails[0].content }</Button>
        </Col>
        <Col lg>
          <Button onClick={()=>{this.sortAd()}} variant="primary" size="lg" block>{ SidebarDetails[1].content }</Button>
        </Col>
        <Col lg>
          <Button onClick={()=>{this.sortYa()}} variant="success" size="lg" block>{ SidebarDetails[2].content }</Button>
        </Col>
        <Col lg>
          <Button onClick={()=>{this.sortYd()}} variant="primary" size="lg" block>{ SidebarDetails[3].content }</Button>
        </Col>
        <Col lg>
          <Button onClick={()=>{this.reset()}} variant="danger" size="lg" block>{ SidebarDetails[4].content }</Button>
        </Col>
      </Row>
    )
}
}

export default Sidebars;
