import React from 'react';
import HomeGamesDetails from '../data/homeGames.json';
import HomeGames from './HomeGames';
import Comments from './Comments';
import CommentsDetails from './CommentsDetails';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Card,  Col,} from 'react-bootstrap';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    useLocation,
    useParams
  } from "react-router-dom";


function Pagination() {
    return (
        <Router>
        <GameSwitch />
        </Router>
    );
}

function GameSwitch() {
let location = useLocation();
let background = location.state && location.state.background;
return (
  <div>
    <Switch location={background || location}>
      <Route exact path="/" children={<HomeGames />} />
      <Route path="/game/:id" children={<GameView />} />
    </Switch>
  </div>
);
}

function GameView() {
    let { id } = useParams();
    let HomeGameDetails = HomeGamesDetails[parseInt(id, 10)];
    return (
        <Col>
            <Card>
                <Card.Img variant="top" src={HomeGameDetails.src} />
                <Card.Body>
                    <Card.Title>
                        {HomeGameDetails.name}
                    </Card.Title>
                    <Card.Text>
                        {HomeGameDetails.releaseYear}
                    </Card.Text>
                    <Card.Text>
                        {HomeGameDetails.studio}
                    </Card.Text>
                    <CommentsDetails />
                </Card.Body>               
            </Card>
        </Col>       
    )            
  }



export default Pagination;
