import React from 'react';
import SidebarDetails from '../data/sidebars.json';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col, Button } from 'react-bootstrap';




function Sidebars() {


  return (

      <Row className="mt-4 mb-4 row row-eq-height">
        <Col lg>
          <Button onChange={()=>{this.sortAs()}} variant="success" size="lg" block>{ SidebarDetails[0].content }</Button>
        </Col>
        <Col lg>
          <Button variant="primary" size="lg" block>{ SidebarDetails[1].content }</Button>
        </Col>
        <Col lg>
          <Button variant="success" size="lg" block>{ SidebarDetails[2].content }</Button>
        </Col>
        <Col lg>
          <Button variant="primary" size="lg" block>{ SidebarDetails[3].content }</Button>
        </Col>
        <Col lg>
          <Button variant="danger" size="lg" block>{ SidebarDetails[4].content }</Button>
        </Col>
      </Row>
  )
}

export default Sidebars;
