import React from 'react';
import HomeGamesDetails from '../data/homeGames.json';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Card, Row, Col,} from 'react-bootstrap';

function HomeGames() {
    return (

        <Row>
        {HomeGameDetails.map((HomeGameDetail) =>{
            return(
                <Col md={6}>
                <Card>
                    <Card.Img variant="top" src={HomeGameDetail.src} />
                    <Card.Body>
                        <Card.Title>
                            {HomeGameDetail.name}
                        </Card.Title>
                        <Card.Text>
                            {HomeGameDetail.releaseYear}
                        </Card.Text>
                        <Card.Text>
                            {HomeGameDetail.studio}
                        </Card.Text>
                        <Card.Link variant="primary">read more...</Card.Link>
                    </Card.Body>

                </Card>
                </Col>
            )
        })}           
</Row>
    )
}

export default HomeGames;
