import React, {Component} from 'react';
import Pagination from './Pagination';
import HomeGamesDetails from '../data/homeGames.json';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col, Button } from 'react-bootstrap';

class Games extends Component {
  constructor (props){
    super(props)
    this.sortYa = this.sortYa.bind(this)
    this.state = {
      HomeGameDetails: HomeGamesDetails
    }
  }

  sortAa(){
    this.setState({
      HomeGameDetails: HomeGamesDetails.sort((a,b)=>a.sd.localeCompare(b.sd))
    })
  };

  sortAd(){
    this.setState({
      HomeGameDetails: HomeGamesDetails.sort((a,b)=>b.sd.localeCompare(a.sd))
    })
  };

  sortYa(){
    this.setState({
      HomeGameDetails: HomeGamesDetails.sort((a,b)=>a.releaseYear-b.releaseYear)
    })
  };

  sortYd(){
    this.setState({
      HomeGameDetails: HomeGamesDetails.sort((a,b)=>b.releaseYear-a.releaseYear)
    })
  };

  reset(){
    this.setState({
      HomeGameDetails: HomeGamesDetails.sort((a,b)=>a.id-b.id)
    })
  }

  render(){
  return (  
    <Container text-center="true">
      <h1 className="text-center mb-5 mt-3">Online Games</h1>     
      <Row className="mt-4 mb-4 row-eq-height">
        <Col lg>
          <Button onClick={()=>{this.sortAa()}} size="lg" block
          className="btn btn-block btn-success d-flex flex-sm-column align-items-center justify-content-left justify-content-sm-center"
          >ascending alphabetical</Button>
        </Col>
        <Col lg>
          <Button onClick={()=>{this.sortAd()}} variant="primary" size="lg" block
          className="btn btn-block btn-primary d-flex flex-sm-column align-items-center justify-content-left justify-content-sm-center"
          >descending alphabetical</Button>
        </Col>
        <Col lg>
          <Button onClick={(e)=>{this.sortYa()}} variant="success" size="lg" block
          class="btn btn-block btn-primary d-flex flex-sm-column align-items-center justify-content-left justify-content-sm-center"
          >release date ascending</Button>
        </Col>
        <Col lg>
          <Button onClick={(e)=>{this.sortYd(e)}} variant="primary" size="lg" block
          class="btn btn-block btn-primary d-flex flex-sm-column align-items-center justify-content-left justify-content-sm-center"
          >release date descending</Button>
        </Col>
        <Col lg>
          <Button onClick={()=>{this.reset()}} variant="danger" size="lg" block
          class="btn btn-block btn-primary d-flex flex-sm-column align-items-center justify-content-left justify-content-sm-center"
          >reset</Button>
        </Col>
      </Row>

        <Pagination />

    </Container>
  );
}
}

export default Games;
